/* eslint-disable import/no-anonymous-default-export */
import React, { useContext, useEffect, useState } from 'react'
import Task from './Task/Task'
import { MyContext } from './Context/MyContext'


export default function () {
  const {tasks,fetchTasks} = useContext(MyContext)
  const [sortedTasks, setSortedTasks] = useState([]);
  // eslint-disable-next-line react-hooks/exhaustive-deps
  useEffect(() => fetchTasks(),[])
   useEffect(() => {
    // Sort tasks by scheduled date and time, then by priority
    const sorted = Object.values(tasks).sort((a, b) => {
      const dateComparison = new Date(a.scheaduleDateTime) - new Date(b.scheaduleDateTime);
      if (dateComparison === 0) {
        return a.taskPriority - b.taskPriority;
      }
      return dateComparison;
    });
    setSortedTasks(sorted);
  }, [tasks])
  return (
    <div>
        {sortedTasks.map(
            key => <Task key ={key} task = {tasks[key]} />
        )}
    </div>
  )
}
